export function getTechById(id) {
    return TECHNOLOGIES.find(tech => tech.id === id);
}
export function getTechsByEra(era) {
    return TECHNOLOGIES.filter(tech => tech.era === era);
}
export function canResearch(techId, researchedTechs, science) {
    const tech = getTechById(techId);
    if (!tech)
        return false;
    if (researchedTechs.has(techId))
        return false;
    // Check prerequisites
    for (const prereq of tech.prerequisites) {
        if (!researchedTechs.has(prereq))
            return false;
    }
    // Check cost
    return science >= tech.cost.science;
}
